function Check(){var b=/^[a-zA-Z0-9]{6,18}$/;var d=document.getElementById("username");var a=document.getElementById("password");var c=document.getElementById("new_password");if(d.value===""){alert("用户名不能为空，请重新填写！");return false}else{if(!b.test(d.value)){alert("请正确输入用户名！");d.value="";d.focus();return false}else{if(c.value===""){alert("密码不能为空，请重新输入！");return false}else{if(!b.test(c.value)){alert("请正确输入新密码，由6-18位字母和数字的混合组成");c.value="";c.focus();return false}else{if(a.value!==c.value){alert("两次输入不一致，请重新确认密码！");a.value="";a.focus();return false}}}}}return true};